/**
 * @brief Public
 *
 * @author MMI Configuration Tool
 * @date 2004-11-2
 * @param T_MMI_KEYPAD phyKey: physical key ID
 * @return T_eFUNC_KEY
 * @retval 
 */
T_eFUNC_KEY MappingPublicKey(T_MMI_KEYPAD phyKey)
{
    if (phyKey.pressType == PRESS_SHORT)
    {
        switch (phyKey.keyID) {
        case kbVOICE_UP:        return fkPUBLIC_VOICE_UP;
        case kbVOICE_DOWN:      return fkPUBLIC_VOICE_DOWN;
#ifdef CHRONTEL
		case kbSWX: 			return fkPUBLIC_TV_EN;
		case kbSWY: 			return fkPUBLIC_TV_SWITCH;
		
#endif

#ifdef SUPPORT_TVOUT        
        case kbSWX:             return fkPUBLIC_TV_EN;
        case kbSWY:             return fkPUBLIC_TV_SWITCH;
#endif        
        default:                break;
        }
    } 
    else if (phyKey.pressType == PRESS_LONG)    /* hold */
    {
        switch (phyKey.keyID) {
#if (defined(SPIBOOT) && defined(SUPPORT_TVOUT))
		case kbVOICE_UP:		return fkPUBLIC_TV_EN;
		case kbVOICE_DOWN:		return fkPUBLIC_TV_SWITCH;
#else
#ifdef CHIP_AK3753
        case kbVOICE_UP:        return fkPUBLIC_TV_EN;
        case kbVOICE_DOWN:      return fkPUBLIC_TV_SWITCH;
#else
	    case kbVOICE_UP:        return fkPUBLIC_VOICE_UP;
        case kbVOICE_DOWN:      return fkPUBLIC_VOICE_DOWN;
#endif
#endif
//      case kbCLEAR:           return fkPUBLIC_POWER_OFF;
        case kbMENU:            return fkPUBLIC_KEY_LOCK;

        default:                break;
        }
    }
    return fkNULL;
} /* end MappingPublicKey(T_MMI_KEYPAD phyKey) */

