/**
 * @brief mapping ebook key
 *
 * @author Heying
 * @date 2007-12-5
 * @param T_MMI_KEYPAD phyKey: physical key ID
 * @return T_EB_ACTION
 * @retval 
 */

T_EB_ACTION MappingEbookKey(T_MMI_KEYPAD phyKey)
{
    if (phyKey.pressType == PRESS_SHORT)
    {
        switch (phyKey.keyID)
        {
            case kbUP:
                return EB_UP_LINE;
            case kbDOWN:
                return EB_DOWN_LINE;
            case kbLEFT:
                return EB_UP_PAGE;
            case kbRIGHT:
                return EB_DOWN_PAGE;
            case kbOK:
                return EB_DOWN_PAGE;
            case kbCLEAR:
                return EB_RETURN;
            case kbMENU:
                return EB_MENU;
            default:
                break;
        }
    }
    else if (phyKey.pressType == PRESS_LONG)    /* hold */
    {
        switch (phyKey.keyID)
        {
            case kbUP:
                return EB_GO_TOP;
            case kbDOWN:
                return EB_DOWN_LINE;
            case kbLEFT:
                return EB_UP_PAGE;
            case kbRIGHT:
                return EB_DOWN_PAGE;
            case kbCLEAR:
                return EB_RETURN_HOME;
            case kbOK:
                return EB_GO_TOP;
            default:
                break;
        }
    }
    return 0;
}                               /* end MappingEbookKey(T_MMI_KEYPAD phyKey) */

